var MMC2_Snapins = MMC2_Snapins || [];

MMC2_Snapins.push({
    "name": "nitro_scopes",
    "create": function( placeIn ) {
        var $namespace = {};
    
        var $export = function(objectID, objectData) {
            $namespace[objectID] = objectData;
            return objectData;
        };
        var $import = function(objectID) {
            return $namespace[objectID] || (function() {
                throw "Namespace " + objectID + " is not defined (yet?)";
            })();
        };
        var $pid = getUID();
    
        var dlg = $export("0001-dlg", (new Dialog({
            "alwaysOnTop": false,
            "appIcon": "data:image/png;base64,",
            "caption": "Nitro Cloud Scopes",
            "closeable": false,
            "maximizeable": true,
            "maximized": false,
            "minHeight": 50,
            "minWidth": 50,
            "minimizeable": true,
            "modal": false,
            "moveable": false,
            "resizeable": false,
            "scrollable": false,
            "visible": true,
            "x": 4,
            "y": 0,
            "childOf": placeIn
        })).chain(function() {
            Object.defineProperty(this, "pid", {
                "get": function() {
                    return $pid;
                }
            });
            this.addClass("PID-" + $pid);
        }));
    
        $export("0001-img", (new DOMImage({
            "src": "data:image/png;base64,",
            "displayMode": "best"
        })).setAttr("style", "top: 10px; left: 10px; width: 32px; height: 32px; position: absolute; border-color: transparent"));
    
        $export("0001-lbl", (new DOMLabel("Available Scopes in your company")).setAttr("style", "top: 20px; left: 50px; width: 570px; position: absolute; text-overflow: ellipsis; font-size: 1.3em; font-weight: bold"));
    
        $export("0002-lbl", (new DOMLabel("Available cloud scopes")).setAttr("style", "top: 50px; left: 10px; width: 150px; position: absolute; text-overflow: ellipsis"));
    
        dlg.grid = $export("0001-grid", (new DataGrid()).setAttr("style", "top: 75px; left: 10px; position: absolute").setAnchors({
            "width": function(w, h) {
                return w - 20 + "px";
            },
            "height": function(w, h) {
                return h - 90 + "px";
            }
        }).setProperty("selectable", true).chain(function() {
            this.colWidths = [80, 150, 100];
            this.th(["Scope ID", "Scope Name", "Servers Assigned"]);
            this.setProperty("delrow", function(row) {
                //What to do when deleting a row
                return false;
            });
            this.enableResizing();
            this.enableSorting();
        }));
    
        $export("0001-toolbar", [{
            "name": "Tools",
            "items": [
                {
                    "caption": "Refresh",
                    "id": "cmd_scope_refresh",
                    "icon": "img/mmc2/nitro/refresh-48x48.png",
                    "handler": dlg.appHandler
                }
            ]
        } , {
            "name": "Edit",
            "items": [{
                "caption": "Add new scope",
                "id": "cmd_scope_add",
                "icon": "data:image/png;base64,",
                "handler": dlg.appHandler
            }, {
                "caption": "Remove scope",
                "id": "cmd_scope_remove",
                "icon": "data:image/png;base64,",
                "handler": dlg.appHandler
            }, {
                "caption": "Scope Properties",
                "id": "cmd_scope_edit",
                "icon": "data:image/png;base64,",
                "handler": dlg.appHandler
            }]
        }].chain(function() {
            dlg.toolbars = this;
            var toolbar = dlg.toolbars;
            if (toolbar) {
                toolbar.setIconSize("large");
                toolbar.setButtonLabels(true);
                toolbar.setTitles(false);
            }
        }));
    
        $import("0001-dlg").insert($import("0001-img"));
        $import("0001-dlg").insert($import("0001-lbl"));
        $import("0001-dlg").insert($import("0002-lbl"));
        $import("0001-dlg").insert($import("0001-grid"));
    
        setTimeout(function() {
            dlg.paint();
            dlg.ready();
        }, 1);

        dlg.DOManchors = {
            "dummy": function(w,h) {
                this.width = w - 6;
                this.height = h - 2;
            }
        };

        dlg.handler = 'vfs/0/lib/mmc2/handler-nitro-scopes.php';

        MMC2_NitroScopes_cmd_scope_add( dlg );

        return dlg;

    },
    "activateContext": /^cmd_nitro_scope(\:|$)/
});